function bgiframe(e){
    var iframe = '<iframe class="ifr"></iframe>';
    var target = $(e);
    target.prepend(iframe);
    $('.ifr').css({
       'position' : 'fixed',
        'top' : target.offset().top,
        'left' : target.offset().left,
        'width' : target.width(),
        'height' : target.height(),
    });
};